import React, { useState, useEffect } from "react";
import { ReactSortable } from "react-sortablejs";
import GridItem from "./GridItem";
import GridAddItem from "./GridAddItem";

const images = [
  {
    id: "1",
    image: "images/image-1.webp",
    checked: false,
  },
  {
    id: "2",
    image: "images/image-2.webp",
    checked: false,
  },
  {
    id: "3",
    image: "images/image-3.webp",
    checked: false,
  },
  {
    id: "4",
    image: "images/image-4.webp",
    checked: false,
  },
  {
    id: "5",
    image: "images/image-5.webp",
    checked: false,
  },
  {
    id: "6",
    image: "images/image-6.webp",
    checked: false,
  },
  {
    id: "7",
    image: "images/image-7.webp",
    checked: false,
  },
  {
    id: "8",
    image: "images/image-8.webp",
    checked: false,
  },
  {
    id: "9",
    image: "images/image-9.webp",
    checked: false,
  },
  {
    id: "10",
    image: "images/image-10.jpeg",
    checked: false,
  },
  {
    id: "11",
    image: "images/image-11.jpeg",
    checked: false,
  },
];

const Grid = () => {
  // Drag and Drop Handler
  const onDragDropEnds = (oldIndex, newIndex) => {
    console.log("Drag and drop other tasks");
    console.log(oldIndex, newIndex);
  };

  const [users, setUsers] = useState(images);
  return (
    <div className="border rounded-3 p-3 shadow">
      {users.length === 0 ? (
        <GridAddItem />
      ) : (
        <ReactSortable
          list={users}
          setList={(newlist) => setUsers(newlist)}
          ghostClass="dropArea"
          handle=".dragHandle"
          filter=".ignoreDrag"
          preventOnFilter={true}
          className="grid-container"
          onEnd={({ oldIndex, newIndex }) => onDragDropEnds(oldIndex, newIndex)}
        >
          <>
            {users.map((user, index) => (
              <GridItem key={user.id} user={user} index={index} />
            ))}
            <GridAddItem />
          </>
        </ReactSortable>
      )}
    </div>
  );
};

export default Grid;
